import axios from 'axios';

const Post = ({id}) => {
  return (
    <div>
      <h1>Post #{id}</h1>
    </div>
  )
}

Post.getInitialProps = async ({query}) => {
  const res = await axios.get(`https://jsonplaceholder.typicode.com/comments?postId=${query}`)
  return query;
}


export default Post;
